<?phpnamespace App\Http\Middleware;use App\Enums\Roles;use Closure;use Illuminate\Http\Request;use Illuminate\Support\Facades\Auth;class AdminMiddleware{    /**     * Handle an incoming request.     *     * @param \Illuminate\Http\Request $request     * @param \Closure $next     * @return \Illuminate\Contracts\Foundation\Application|\Illuminate\Http\RedirectResponse|\Illuminate\Routing\Redirector|mixed     */    public function handle (Request $request, Closure $next)    {        if ( Auth::user() && Auth::user()->role_id === Roles::ADMIN ) {            return $next($request);        }        return redirect("/login");    }}