<?phpnamespace App\Http\Middleware;use App\Enums\Roles;use Closure;use Illuminate\Http\Request;use Illuminate\Support\Facades\Auth;class TeacherMiddleware{    /**     * Handle an incoming request.     *     * @param \Illuminate\Http\Request $request     * @param \Closure $next     * @return mixed     */    public function handle (Request $request, Closure $next)    {        if ( Auth::check() && Auth::user()->role_id === Roles::TEACHER ) {            return $next($request);        }        return redirect("/login");    }}