<?phpnamespace App\Http\Requests;use Illuminate\Foundation\Http\FormRequest;class TeacherRequest extends FormRequest{    /**     * Determine if the user is authorized to make this request.     *     * @return bool     */    public function authorize ()    {        return true;    }    /**     * Get the validation rules that apply to the request.     *     * @return array     */    public function rules ()    {        return [            'name_group' => 'required',            'technology' => 'required',            'schedule' => 'required',            'age' => 'required',            'year' => 'required',        ];    }    public function messages ()    {        return [            'name_group.required' => 'Укажите название группы',            'technology.required' => 'Укажите технологию, изучаемые в группе',            'schedule.required' => 'Укажите свое расписание',            'age.required' => 'Укажите возраст учеников',            'year.required' => 'Укажите год обучения',        ];    }}