<?phpnamespace App\Repositories\Implementations;use App\Models\Group;use App\Models\StudentGroups;use App\Repositories\Interfaces\IStudentGroupsRepository;use Illuminate\Support\Collection;class StudentGroupsRepository implements IStudentGroupsRepository{    public function getByStudentId (int $id): Collection    {        return StudentGroups::where('student_id', $id)->get();    }    public function getGroupsInformation ($student_groups): array    {        $student_groups_information = [];        foreach ( $student_groups as $student_group ) {            $groups = Group::where('id', '=', $student_group->group_id)->get();            foreach ( $groups as $group ) {                                $student_groups_information[] = [                    'balance' => $student_group->balance,                    'bonuses' => $student_group->count_bonus_lessons,                    'group' => $group                ];            }        }        return $student_groups_information;    }}