alue = parseInt(el.dataset.id);        })      })      let open = false;      document.querySelector('#info_open').addEventListener('click', () => {        if (!open) {          document.querySelector('#info').style = "display:block";          open = true;        } else {          document.querySelector('#info').style = "display:none";          open = false;        }      })    </script>@endsection